INSERT INTO product_entity VALUES
    (1, 'Смартфон Apple iPhone 11 − это сочетание стиля и функциональности. Аппарат порадует своей производительностью и отсутствием задержек, поскольку в его основе лежит процессор A13 Bionic с шестиядерной конфигурацией.', '/images/1/1.jpg', 'Телефон Iphone 11', 65999),
    (2, 'Смартфон Xiaomi Redmi Note 11 оснащен AMOLED-экраном 6,43 дюйма с разрешением FullHD+ и частотой обновления до 90 Гц. Аккумулятор Note 11 имеет емкость 5000 мАч и поддерживает быструю зарядку через USB-C на мощности 33 Вт.', '/images/2/1.jpg', 'Телефон Xiaomi Note 11', 27999),
    (3, 'Наушники Apple Airpods – ваш проводник в мир цифрового звука. Благодаря фирменному чипу Apple H1 и динамическим излучателям с поддержкой широкого частотного диапазона модель подарит вам высокодетализированное и объемное звучание.', '/images/3/1.jpg', 'Наушники Apple AirPods', 21999),
    (4, 'Благодаря тонким рамкам (10.2 мм) планшета Samsung Galaxy Tab A8 вы сможете полностью погрузиться в происходящее на большом 10.5" экране. Наслаждайтесь любимыми фильмами и контентом вместе с Galaxy Tab A8.', '/images/4/1.jpg', 'Планшет Samsung Galaxy Tab A8', 33499),
    (5, 'Ноутбук Acer Aspire 3 A315-23-R8D5 оптимален для пользователей, которым требуется функциональное и надежное устройство. Высокую скорость загрузки и обработки данных обеспечивает 4-ядерный процессор AMD Ryzen 5.', '/images/5/1.jpg', 'Ноутбук Acer Aspire 5', 79999),
    (6, 'Робот-пылесос Honor HOR-001 предназначен для сухой и влажной уборки. Благодаря мощности всасывания, достигающей 2600 Па, он собирает даже крупный мусор. Объем контейнера составляет 0,51 л.', '/images/6/1.jpg', 'Робот-пылесос HONOR-HOR', 18999),
    (7, 'Электрический самокат-мопед iconBIT Trident T1 с 12-дюймовыми колесами дополнен удобным сиденьем и багажной корзиной. Он способен разогнаться до 30 км/ч, скорость легко регулируется. Корпус выдерживает нагрузку до 120 кг', '/images/7/1.jpg', 'Электрический-самокат iconBIT T1', 44999),
    (8, 'Холодильник с нижней морозильной камерой Indesit DS 316 с хорошо продуманной внутренней планировкой позволяет распределить все продукты в соответствии с потребностями пользователя и требованиям гигиены.', '/images/8/1.jpg', 'Холодильник Indesit DS 316', 32399),
    (9, 'Благодаря размерам 34 х 24 х 43 см кофемашина DeLonghi ECAM22.110.B не занимает много места. Она быстро приготовит для вас классический эспрессо, крепкий лунго или капучино с красивой молочной пеной.', '/images/9/1.jpg', 'Кофемашина DeLonghi ECAM22', 34499),
    (10, 'Honor 50 Lite – смартфон с квадрокамерой и модулем бесконтактной оплаты услуг NFC, работает на операционной системе Android 11 с поддержкой сервисов Google.', '/images/10/1.jpg', 'Смартфон HONOR 50 lite', 21999),
    (11, 'Смарт-часы Samsung Wise сочетают стильный дизайн и передовые технологии мониторинга показателей здоровья. Станут верным и надёжным помощником в обычной жизни и спорте.', '/images/11/1.jpg', 'Смарт-часы Samsung Galaxy Watch4 Classic', 29999),
    (12, 'Haier HW60 – узкая стиральная машина, рассчитанная на 6 кг белья. Она оборудована барабаном Pillow, внутренняя поверхность которого покрыта отверстиями особой формы. За счёт этого вещи не спутываются, а ткани не повреждаются.', '/images/12/1.jpg', 'Стиральная машин Haier HW60', 49999),
    (13, 'Блендер Braun HB701AI-MQ7045X поможет вам в решении самых разных кухонных задач и ускорит приготовление множества блюд. Вы сможете самостоятельно устанавливать скорость работы, действуя интуитивно – за это отвечает технология Smart Speed.', '/images/13/1.jpg', 'Погружной блендер Braun HB701AI', 9999),
    (14, 'Микроволновая печь LG MS259 серии NeoChef позволит вам значительно разнообразить свой рацион и даст возможность ежедневно радовать родных и близких шедеврами кулинарии.', '/images/14/1.jpg', 'Микроволновая печь соло LG MS259', 19999),
    (15, 'Геймпад для Xbox Microsoft Elite v2 оригинальное устройство от легендарного производителя и разработчика. Модель обладает совместимостью с платформой Xbox ONE. Подключение – беспроводное, с помощью Bluetooth.', '/images/15/1.jpg', 'Геймпад для Xbox Microsoft Elite v2', 18999),
    (16, 'Наслаждайтесь глубоким, естественным и реалистичным контентом. Революционный процессор телевизора Sony KD85X85TJ использует данные о человеческом восприятии для перекрестного анализа и мгновенной оптимизации элементов.', '/images/16/1.jpg', 'Телевизор Sony KD85X85TJ', 259999);